INSERT INTO `zuk_article` VALUES ('31', '1', '78', '号称最好的系统不卡顿', '联想', '', '0', '                            	<div class=\"viewthread_content\" author=\"岁月神偷\" pid=\"4199066\" formhash=\"28977099\">\r\n\r\n<div class=\"viewthread_table\"><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr><td id=\"postmessage_4199066\">号称做好的系统 不卡顿 都是骗人的  定期死机 系统一直都不稳定 联想这么大的企居然连个系统的稳定性都没保障 还跟人家出什么手机啊 </td></tr></tbody></table></div>\r\n\r\n<div class=\"pattl\">\r\n\r\n\r\n<ignore_js_op>\r\n\r\n<dl class=\"attm\">\r\n<dt></dt>\r\n<dd>\r\n\r\n<p class=\"mbn dn\">\r\n<a href=\"/forum.php?mod=attachment&aid=Mjc1MjY2fGZhMDg0NDRifDE0NzkyMDg2NDZ8MHwzMjQ2MjU%3D&nothumb=yes\" onmouseover=\"showMenu({\'ctrlid\':this.id,\'pos\':\'12\'})\" id=\"aid275266\" class=\"xw1 \" target=\"_blank\">Screenshot_2016-11-08-21-35-44-0024394975.png</a><!---->\r\n</p>\r\n<div class=\"tip tip_4\" id=\"aid275266_menu\" style=\"display: none\" disautofocus=\"true\">\r\n<div>\r\n<p>\r\n<span class=\"y\"><span title=\"2016-11-9 09:01\">6 天前</span> 上传</span>\r\n<em class=\"xg1\">2.82 MB</em>\r\n\r\n</p>\r\n</div>\r\n<div class=\"tip_horn\"></div>\r\n</div>\r\n<p class=\"mbn\">\r\n\r\n</p>\r\n\r\n\r\n\r\n<div class=\"mbn savephotop\">\r\n\r\n<a href=\"javascript:;\"><img id=\"aimg_275266\" aid=\"275266\" src=\"http://img.bbs.lenovomobile.cn/forum/201611/09/090144y9hg0tc086xchu6c.png.thumb.jpg\" onclick=\"zoom(this, this.getAttribute(\'zoomfile\'), 0, 0, \'1\')\" zoomfile=\"http://img.bbs.lenovomobile.cn/forum/201611/09/090144y9hg0tc086xchu6c.png\" file=\"http://img.bbs.lenovomobile.cn/forum/201611/09/090144y9hg0tc086xchu6c.png.thumb.jpg\" alt=\"Screenshot_2016-11-08-21-35-44-0024394975.png\" title=\"Screenshot_2016-11-08-21-35-44-0024394975.png\" w=\"1080\"></a>\r\n\r\n</div>\r\n\r\n</dd>\r\n</dl>\r\n\r\n</ignore_js_op>\r\n\r\n\r\n\r\n<ignore_js_op>\r\n\r\n<dl class=\"attm\">\r\n<dt></dt>\r\n<dd>\r\n\r\n<p class=\"mbn dn\">\r\n<a href=\"/forum.php?mod=attachment&aid=Mjc1MjY3fDkxZTg1MDc0fDE0NzkyMDg2NDZ8MHwzMjQ2MjU%3