INSERT INTO `zuk_article` VALUES ('103', '1', '123', '【现形记】联想乐檬K5 Note之拍照篇', '乐檬, 拍照, 相机, 烟火, 天气', '', '0', '                            	<div class=\"viewthread_content\" author=\"云天\" pid=\"3417385\" formhash=\"28977099\">\r\n\r\n<div class=\"viewthread_table\"><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr><td id=\"postmessage_3417385\"><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">亲爱的小伙伴们楼主又回来啦，今天为大家带来的是乐檬</font>k5 note移动版的相机评测。时间步入2016年，即使千元机现在也追求有一个完美的相机。现在拍照对一个手机来说也是人们选择它的一个重要因素。</font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">乐檬</font>k5note相比上一代乐檬k3note也有了长足进步，前置摄像头由500万像素升级到800万像素，可以让你轻松拍出更美的自己。虽然还是采用的1300万像素的手机但是对焦速度有明显的提升。</font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">乐檬</font>k5 note的超级相机有多重拍照模式可以选择人像，风景，烟火等模式能拍出更好的照片</font></font></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\">还可以自己设置对比度，锐度，饱和度，曝光量等拍出自己想要的照片，还可以设置微笑拍照，照片的分辨率。</font></font></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\">如果想拍出艺术范的照片可以使用九宫格构图，这样拍出的照片更好看。</font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">看看楼主拍的风景吧，绿色的行道树，这个夏天就靠它为我们带来阴凉。</font></font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">楼主拍的北京的蓝天，北京出现如此漂亮的天空真是少见，估计以后见到这种天气机会就不多了。</font></font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">乐檬</font>k5 note的微距效果也不错，看看这几张还过得去吧,翠绿的叶子，娇艳的花儿。</font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"></div><div align=\"left\"><strong><font style=\"color:rgb(63, 63, 63)\"><font face=\"宋体\"><font style=\"font-size:16.5pt\"><font face=\"宋体\">最后来几张夜拍，夜间打开闪光灯的效果还是可以的。</font></font></font></font></strong><strong><font style=\"color:rgb(63, 63, 63)\"></font></strong></div><div align=\"left\"></div><br>\r\n</td></tr></tbody></table></div>\r\n\r\n<div class=\"viewthread_locked\">\r\n本帖子中包含更多资源，您需要 <a href=\"/member.php?mod=logging&action=login\" onclick=\"showWindow(\'login\', this.href);return false;\">登录</a> 才可以下载或查看，没有帐号？<a href=\"/passport.php?act=register\" title=\"注册帐号\">注册</a> </div>\r\n\r\n<!--回复帖回复列表-->\r\n<div id=\"comment_3417385\">\r\n</div>\r\n\r\n</div>                            ', '1479281894', '0');
