INSERT INTO `zuk_article` VALUES ('106', '1', '114', '兑金堂堂规', '电话, 掌柜, Z码, 联想, ZUKer', '', '0', '                            	<div class=\"viewthread_content\" author=\"奎木\" pid=\"2924964\" formhash=\"28977099\">\r\n\r\n<div class=\"viewthread_table\"><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr><td id=\"postmessage_2924964\"><i class=\"pstatus\"> 本帖最后由 奎木 于 2016-8-2 17:11 编辑 </i><br>\r\n<div align=\"left\"></div><div align=\"left\"><strong><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">【兑金堂是个什么】</font></font></font></strong></div><div align=\"left\"><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">ZUK兑金堂是ZUK社区为广大用户提供增值服务的平台，是为ZUKer谋取福利的所在。在这里，论坛注册用户可以用<a href=\"http://bbs.zuk.cn/gonggao/t123679/\" target=\"_blank\" class=\"relatedlink\">金币</a>兑换各种礼品！</font></font></font></div><br>\r\n<div align=\"left\"><strong><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">【兑金堂都能兑换什么】</font></font></font></strong></div><div align=\"left\"><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">虚拟的金币可以当真金白银兑换实物商品哦！这里你可以兑换到<strong>手机、各种周边配件、联想ZUK独家定制商品、Z码、掌柜签名照等等等等</strong>，各种福利各种惊喜！想要抢到心仪的商品，就赶紧攒金币吧！</font></font></font></div><br>\r\n<div align=\"left\"><strong><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">【如何获得金币】</font></font></font></strong></div><div align=\"left\"><div class=\"viewthread_quote\"><blockquote><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#2f4f4f\"><br>\r\n</font></font></font><blockquote><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#2f4f4f\"><strong><a href=\"http://bbs.zuk.cn/gonggao/t123679/\" rel=\"nofollow\" target=\"_blank\">金币获取秘籍</a></strong></font></font></font></blockquote><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#2f4f4f\">      </font></font></font><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#2f4f4f\"><br>\r\n</font></font></font></blockquote></div><br>\r\n</div><div align=\"left\"><strong><font face=\"微软雅黑\"><font size=\"3\"><font color=\"#696969\">【兑金堂上新规